mapper.select:
select distinct a.biz_cd ,a.inbound_req_dt ,a.inbound_req_no ,a.center_cd ,a.customer_cd ,c.customer_nm ,a.supplier_cd ,d.supplier_nm ,a.inbound_type ,a.inbound_exp_dt ,a.remark ,c.customer_cd ,c.customer_nm , case when a.status = 0 then '대기' when a.status = 1 then '반려' when a.status = 2 then '승인' end status from t_inbound_req a left join t_customer c on a.biz_cd = c.biz_cd and a.customer_cd = c.customer_cd left join t_supplier d on a.biz_cd = d.biz_cd and a.customer_cd = d.customer_cd and a.supplier_cd = d.supplier_cd where a.biz_cd = #{bizCd} and a.center_cd = #{centerCd} and a.inbound_req_dt between #{inboundReqFromDt} and #{inboundReqToDt}
and a.inbound_req_no = #{inboundReqNo}
and a.customer_cd = #{customerCd}
and a.supplier_cd = #{supplierCd}

update.foreach:
UPDATE t_inbound_req
SET STATUS = #{i.status}
WHERE biz_cd = #{i.bizCd} AND inbound_req_dt = #{i.inboundReqDt} AND inbound_req_no = #{i.inboundReqNo}
