mapper.insert:
insert into t_location
        (
             biz_cd
            ,center_cd
            ,location_cd
            ,zone_cd
            ,location_nm
            ,location_type
            ,able_cbm
            ,able_weight
            ,loc_X
            ,loc_Y
            ,loc_Z
            ,use_yn
            ,created_id
            ,created_dt
        )
values (

            #{item.bizCd}
            ,#{item.centerCd}
            ,#{item.locationCd}
            ,#{item.zoneCd}
            ,#{item.locationNm}
            ,#{item.locationType}
            ,#{item.ableCbm}
            ,#{item.ableWeight}
            ,#{item.locX}
            ,#{item.locY}
            ,#{item.locZ}
            ,#{item.useYn}
            ,#{item.createdId}
            ,now()
        )
ON DUPLICATE KEY UPDATE
         zone_cd = #{item.zoneCd},
         location_nm = #{item.locationNm},
         location_type = #{item.locationType},
         able_cbm = #{item.ableCbm},
         able_weight = #{item.ableWeight},
         loc_X = #{item.locX},
         loc_Y = #{item.locY},
         loc_Z = #{item.locZ},
         use_yn = #{item.useYn},
         modified_id = #{item.createdId},
         modified_dt = now()
