mapper.insert:
insert into t_location
        (
             biz_cd
            ,center_cd
            ,location_cd
            ,zone_cd
            ,location_nm
            ,location_type
            ,able_cbm
            ,able_weight
            ,loc_X
            ,loc_Y
            ,loc_Z
            ,use_yn
            ,created_id
            ,created_dt
        )
values (

            #{item.bizCd}
            ,#{item.centerCd}
            ,#{item.locationCd}
            ,#{item.zoneCd}
            ,#{item.locationNm}
            ,#{item.locationType}
            ,#{item.ableCbm}
            ,#{item.ableWeight}
            ,#{item.locX}
            ,#{item.locY}
            ,#{item.locZ}
            ,#{item.useYn}
            ,#{item.createdId}
            ,now()
        )

ON DUPLICATE KEY UPDATE
         zone_cd = VALUE(zoneCd),
         location_nm = VALUE(locationNm),
         location_type = VALUE(locationType),
         able_cbm = VALUE(ableCbm),
         able_weight = VALUE(ableWeight),
         loc_X = VALUE(locX),
         loc_Y = VALUE(locY),
         loc_Z = VALUE(locZ),
         use_yn = VALUE(useYn),
         modified_id = VALUE(createdId),
         modified_dt = now()
