mapper.insert:
insert into t_zone
    (
        biz_cd
        ,center_cd
        ,zone_cd
        ,zone_nm
        ,stage_yn
        ,zone_type
        ,keep_type
        ,use_yn
        ,created_id
        ,created_dt
    )
values

    (
        #{item.bizCd}
        ,#{item.centerCd}
        ,#{item.zoneCd}
        ,#{item.zoneNm}
        ,#{item.stageYn}
        ,#{item.zoneType}
        ,#{item.keepType}
        ,#{item.useYn}
        ,#{item.createdId}
        ,now()
    )
ON DUPLICATE KEY UPDATE
    zone_nm = #{item.zoneNm}
    , stage_yn = #{item.stageYn}
    , zone_type = #{item.zoneType}
    , keep_type = #{item.keepType}
    , use_yn = #{item.useYn}
    , modified_id = #{item.createdId}
    , modified_dt = now()
